ContainedObjects.LuaScript:
owner = nil
tiedGoal = ""

function onsave()
    local data = {}
    data.owner = owner
    data.tiedGoal = tiedGoal
    return JSON.encode(data)
end

function onload(saved_data)
    if saved_data ~= '' then
        local data = JSON.decode(saved_data)
    end
end

ObjectStates.LuaScript:
function onload()
    self.createButton({
        click_function = "toggleEnforceRulesClick",
        function_owner = Global,
        width = 2500,
        height=  800,
        font_size = 300,
        position = {0, 1, 0},
        label = "Enforce Rules: ON",
        tooltip = "Turning this OFF will disable checks for correct amount of discards or military for payment costs."
    })
end

function changeEnforceRulesButtonLabel(value)
    self.editButton({
        index = 0,
        label = "Enforce Rules: " .. (value and "ON" or "OFF")
    })
end
